function removeAllActiveTag(){navItem.forEach(e=>{e.classList.remove("active","active-tag")})}function reloadGallery(e){document.querySelectorAll(".item-column").forEach(a=>{a.style.display="none","all"!==e&&e!==a.children[0].dataset.galleryTag||(a.style.display="block")})}const navItem=document.querySelectorAll(".nav-link");navItem.forEach(e=>{e.addEventListener("click",a=>{removeAllActiveTag(),e.classList.add("active","active-tag"),reloadGallery(a.target.dataset.imagesToggle)})});